import { useEffect, useReducer } from "react";
import {
  fetchData,
  API,
  reducePoints,
  calculatePercentage,
} from "./helpers/Helpers.js";
import Question from "./components/Question.js";
import Header from "./components/Header.js";
import Loader from "./components/Loader.js";
import Error from "./components/Error.js";
import Main from "./components/Main.js";
import Progress from "./components/Progress.js";
import Footer from "./components/Footer.js";
import StartScreen from "./components/StartScreen.js";
import "./index.css";

const initialState = {
  questions: [],
  // loading , error , ready , active , finished
  status: "loading",
  index: 0,
  answer: null,
  points: 0,
};

function reducer(state, action) {
  switch (action.type) {
    case "dataRecieved":
      return {
        ...state,
        questions: action.payload,
        status: "ready",
      };

    case "dataFailed":
      return {
        ...state,
        status: "error",
      };

    case "start":
      return {
        ...state,
        status: "active",
      };

    case "next":
      return {
        ...state,
        index: state.index + 1,
        answer: null,
      };

    case "newAnswer":
      const question = state.questions.at(state.index);
      return {
        ...state,

        answer: action.payload,
        points:
          action.payload === question.correctOption
            ? state.points + question.points
            : state.points,
      };

    default:
      throw new Error("unknown action");
  }
}

function App() {
  const [{ questions, status, index, answer, points }, dispatch] =
    useReducer(reducer, initialState);

  const numberOfQuestions = questions.length;
  const totalPoints = reducePoints(questions);

  useEffect(function () {
    async function fetchDataAndInitialize() {
      try {
        const data = await fetchData(API);

        dispatch({
          type: "dataRecieved",
          payload: data,
        });
      } catch (error) {
        console.error("Error fetching data:", error);
        dispatch({
          type: "dataFailed",
        });
      }
    }

    fetchDataAndInitialize();
  }, []);

  return (
    <div className="app">
      <Header />
      <Main>
        {status === "loading" && <Loader />}
        {status === "error" && <Error />}
        {status === "ready" && (
          <StartScreen
            numberOfQuestions={numberOfQuestions}
            dispatch={dispatch}
          />
        )}
        {status === "active" && (
          <>
            <Progress
              numberOfQuestions={numberOfQuestions}
              index={index}
              totalPoints={totalPoints}
              points={points}
            />

            <Question
              question={questions[index]}
              dispatch={dispatch}
              answer={answer}
            />

            <Footer dispatch={dispatch} answer={answer} />
          </>
        )}
        {status === "ready" && (
          <>
            <p className="result">
              You scored {points} out of {totalPoints}{" "}
              {calculatePercentage(points, totalPoints)}%
            </p>
            <p></p>
            <button
              className="btn btn-ui"
              onClick={() =>
                dispatch({
                  type: "next",
                })
              }
            >
              Next
            </button>
          </>
        )}
      </Main>
    </div>
  );
}

export default App;
