import { fetchData, reducePoints, API } from "./helpers/Helpers.js";
import Header from "./components/Header.js";
import Loader from "./components/Loader.js";
import Error from "./components/Error.js";
import Main from "./components/Main.js";
import "./index.css";
import { useEffect, useReducer } from "react";

const initialState = {
  questions: [],
  // loading , error , ready , active , finished
  status: "loading",
};

function reducer(state, action) {
  switch (action.type) {
    case "dataRecieved":
      return {
        ...state,
        questions: action.payload,
        status: "ready",
        numberOfQuestions: action.payload.length,
        currentQuestion: 0,
        totalPoints: reducePoints(action.payload),
      };

    default:
      throw new Error("unknown action");
  }
}

function App() {
  const [{ questions, status }, dispatch] = useReducer(
    reducer,
    initialState
  );

  useEffect(function () {
    async function callback() {
      try {
        const data = await fetchData(API);

        dispatch({
          type: "dataRecieved",
          payload: data,
        });
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    }

    callback();
  }, []);
  console.log(state);

  return (
    <div className="app">
      <Header />

      <Main>
        {state.status === "loading" ? (
          <Loader />
        ) : (
          <p>you are ready</p>
        )}
      </Main>
    </div>
  );
}

export default App;
